{
    "tiddlers": {
        "$:/CleverNote/ui/Stylesheet": {
            "text": "<$reveal state="$:/CurrentPlatform" type="nomatch" text="desktop">

.bottombarcontainer {
	position: fixed;
	width: 100%;
	height: 8%;
	background-color: #F8F8F8;
	bottom: 0;
	left: 0;
	z-index: 2000;
	display: flex;
	align-items: center;
	justify-content: center;
}
.bottombar {
	width: 100%;
	display: flex;
	align-items: center;
	justify-content: center;
}

.leftsidebarcontainer-mobile {
	position: fixed;
	left: 0;
	top: 0;
	height: 100%;
	width: 15%;
       max-width: 75px;
	z-index: 1500;
	display: flex;
	align-items: center;
	justify-content: center;
	background-color: #F8F8F8;

}

.leftsidebarcontainer-mobile {
	position: fixed;
	left: 0;
	top: 0;
	height: 100%;
	width: 15%;
	z-index: 1500;
	display: flex;
	align-items: center;
	justify-content: center;
       background-color: #F8F8F8;
}

.leftsidebar-mobile {
	justify-content: center;
	display: flex;
	flex-direction: column;
}

.switch-container {
	position: fixed;
	bottom: 5px;
	left: 5px;
	z-index: 2500;
}

.zaah-listbar-container-mobile {
	position: fixed;
	left: 15%;
	top: 0;
	background: #fff;
	height: 100%;
	z-index: 200;
	padding: 24px 0 0 0;
	width: 100%;
        overflow: auto;
}


@media screen and (min-width: 500px) {
.zaah-listbar-container-mobile {
left:75px;
}
}

.zaah-story-river {
	left: 0;
	width: 100%;
}
.tc-tiddler-frame {
	padding: 28px 20px 42px 22px;
	width: 100%;
}


.zaah-btn-square {
	height: 36px;
	width: 36px;
}

.searchbar-container {
	z-index:1800;
	position: fixed;
	top: 0;
	left: 0;
	background: #f4f4f4;
	width: 100%;
}
.searchbar {
	width: 100%;
	height: 50px;
	z-index: 2500;
}

.jd-searchbar {
	width: calc(100% - 20px);
	height: 30px;

	font-size: 16px !important;
	background: transparent !important;
	padding: 0 140px 0 10px;
	margin: 10px;
}

input.jd-searchbar {
        border: none;
	border-bottom-width: medium;
	border-bottom-style: none;
	border-bottom-color: currentcolor;
	border-bottom: 2px solid #5778d8;
}

.searchresults-mobile {
	width: 100%;
	padding-left: 5%;
	padding-bottom: 2%;
}

.searchresultbuttons svg {
	height: 20px;
	width: 20px;
}



/* Editor Toolbar*/

.tc-edit-tags {
  margin-bottom:12px
}

.tc-editor-toolbar {
  position: -webkit-sticky;
  position: sticky;
  top: 0px; /* distance to stick from top, e.g if you use top bar */
  left:0;
  width:100%;
  font-size:1em;
  line-height:0;
  padding:0;
  border:5px solid transparent;
  background:white;
  margin-top:-11px; /* pull it up */
}
.tc-editor-toolbar:hover {
  border-color:white;

}
.tc-editor-toolbar button {
 display:inline-block;
  padding:0px;
}


/* Sidebar*/
.zaah-listbar-container-mobile .tc-sidebar { padding: 5% 0 0 3%; }


/*Edit Template*/
.tc-tiddler-frame.tc-tiddler-edit-frame {
	padding: 28px 5px 42px 5px;
}

.zaah-btn-edit-template svg {
	height: 20px;
}

<$reveal type="match" state="$:/state/edittemplatefield" text="hide">
.tc-edit-field-add, .tc-edit-fields {display:none;}

</$reveal>

<$reveal type="match" state="$:/state/edittemplatetype" text="hide">
.tc-type-selector {display:none;}

</$reveal>

.tc-block-dropdown {
	position: fixed;
	top: 10%;
	left: 0;
	padding: 2%;
	width: 100%;
	overflow: auto;
	max-height: 50%;
}

</$reveal>",
            "title": "$:/CleverNote/ui/Stylesheet",
            "tags": "$:/tags/Stylesheet",
            "modified": "20171209102717935",
            "created": "20171207163956055"
        },
        "$:/CleverNote/ui/Searchbar": {
            "text": "\define searchresults()
<$action-navigate $to=<<navigateTo>>/>
<$action-setfield $tiddler="$:/state/mobile-searchbar" $value="no"/>
\end


<$reveal state="$:/state/sidebar" type="match" text="no">
<$reveal state="$:/state/mobile-searchbar" type="match" text="yes">
<div class="searchbar-container">
<div class="searchbar">
<$edit-text tiddler="$:/temp/search" type="search" tag="input" placeholder={{$:/language/Search/Search}} class="jd-searchbar" focus="yes"/>
</div>


<$reveal state="$:/temp/search" type="nomatch" text="">
<div class="searchresults-mobile">
<div class="searchresultbuttons">
<$button tooltip={{$:/language/Buttons/AdvancedSearch/Hint}} aria-label={{$:/language/Buttons/AdvancedSearch/Caption}} class="tc-btn-invisible">
<$action-setfield $tiddler="$:/temp/advancedsearch" text={{$:/temp/search}}/>
<$action-setfield $tiddler="$:/temp/search" text=""/>
<$action-navigate $to="$:/AdvancedSearch"/>
{{$:/core/images/advanced-search-button}}
</$button>
<$button class="tc-btn-invisible" tooltip="Clear searchbar">
<$action-setfield $tiddler="$:/temp/advancedsearch" text=""/>
<$action-setfield $tiddler="$:/temp/search" text=""/>
{{$:/core/images/close-button}}
</$button>
</div>

<$linkcatcher actions=<<searchresults>>>
''Tiddlers''<br/>
<$list filter="[!is[system]search{$:/temp/search}sort[title]limit[250]] -[[$:/temp/search]]">
<$link><$view field="title"/></$link><br/>
</$list>


<br/><br/>''System''<br/>
<$list filter="[is[system]search{$:/temp/search}sort[title]limit[250]] -[[$:/temp/search]] -[[$:/temp/advancedsearch]]">
<$link><$view field="title"/></$link><br/>
</$list>

<br/><br/>''Shadows''<br/>
<$list filter="[is[shadow]search{$:/temp/search}sort[title]limit[250]] -[[$:/temp/search]] -[[$:/temp/advancedsearch]]">
<$link><$view field="title"/></$link><br/>
</$list>
</$linkcatcher>


</div>
</$reveal>
</div>
</$reveal>
</$reveal>
",
            "title": "$:/CleverNote/ui/Searchbar",
            "tags": "$:/tags/MobilePageTemplate",
            "modified": "20171209054545225",
            "created": "20171209044039555"
        },
        "$:/CleverNote/ui/PageTemplate/Switch": {
            "text": "<div class="switch-container">
<div class="switch">

<$button class="zaah-btn-rounded">{{$:/core/images/menu-button}}
<$reveal type="match" state="$:/state/sidebar" text="yes">
<$action-setfield $tiddler="$:/state/sidebar" $value="no"/>
</$reveal>
<$reveal type="match" state="$:/state/sidebar" text="no">
<$action-setfield $tiddler="$:/state/sidebar" $value="yes"/>
</$reveal>
</$button>
</div>
</div>",
            "title": "$:/CleverNote/ui/PageTemplate/Switch",
            "tags": "$:/tags/MobilePageTemplate",
            "modified": "20171208100300137",
            "created": "20171208042757688"
        },
        "$:/CleverNote/ui/PageTemplate/ListBar": {
            "text": "<$reveal state="$:/state/sidebar" type="match" text="yes" retain="yes">
<div class="zaah-listbar-container-mobile">
	<$tiddler tiddler={{$:/state/tab/sidebar}}>
	<div class="inline21caps"><$transclude field="caption"/></div>
	{{!!text}}
	</$tiddler>
</div>
</$reveal>",
            "title": "$:/CleverNote/ui/PageTemplate/ListBar",
            "tags": "$:/tags/MobilePageTemplate",
            "modified": "20171209080120562",
            "created": "20171208063100705"
        },
        "$:/CleverNote/ui/PageTemplate/LeftSideBar": {
            "text": "\define sidebar-config-title()
$:/config/sidebar/Visibility/$(currentTiddler)$
\end
<$reveal state="$:/state/sidebar" type="match" text="yes">
<div class="mobile-sidebar">
<div class="leftsidebarcontainer-mobile">
<div class="leftsidebar-mobile">
<$list filter='[all[shadows+tiddlers]tag[$:/tags/SideBar]!has[draft.of]]'>
				<$reveal type="nomatch" state=<<sidebar-config-title>> text="hide">
					<$button class="zaah-btn-rounded" tooltip={{!!caption}} set="$:/state/tab/sidebar" setTo=<<currentTiddler>>>
					<$transclude tiddler={{!!icon}}/>
					</$button>
				</$reveal>
		</$list>
</div>
</div>
</div>
</$reveal>",
            "title": "$:/CleverNote/ui/PageTemplate/LeftSideBar",
            "tags": "$:/tags/MobilePageTemplate",
            "modified": "20171208122029757",
            "created": "20171207170503223"
        },
        "$:/CleverNote/ui/PageTemplate/BottomBar": {
            "text": "\define pagecontrol-config-title()
$:/config/PageControlButtons/Visibility/$(listItem)$
\end
<$reveal state="$:/state/sidebar" type="match" text="no">
<div class="bottombarcontainer">
<div class="bottombar">
<$list filter="[all[shadows+tiddlers]tag[$:/tags/PageControls]!has[draft.of]]" variable="listItem">
						<$reveal type="nomatch" state=<<pagecontrol-config-title>> text="hide">
							<$set name="tv-config-toolbar-class" value="zaah-btn-square">
							<$transclude tiddler=<<listItem>> mode="inline"/>
							</$set>
</$reveal>
</$list>
<$set name="tv-config-toolbar-class" value="zaah-btn-square">
<$transclude tiddler="$:/CleverNote/buttons/search" mode="inline"/>
</$set>
</div></div>
</$reveal>",
            "title": "$:/CleverNote/ui/PageTemplate/BottomBar",
            "tags": "$:/tags/MobilePageTemplate",
            "modified": "20171209032753905",
            "created": "20171207133616205"
        },
        "$:/CleverNote/ui/EditTemplateOptions": {
            "text": "<$reveal state="$:/CurrentPlatform" type="nomatch" text="desktop">
<$button class="tc-btn-invisible zaah-btn-edit-template" popup="$:/state/editTemplateItems">{{$:/core/images/options-button}}</$button>

<$reveal state="$:/state/editTemplateItems" type="popup">
<div class="tc-drop-down" style="position: fixed;top: 10%;left: 0;width: 100%;margin: 0;">
<$list filter="[tag[$:/tags/EditTemplateOptions]]">
<$set name="tv-config-toolbar-class" value="tc-btn-invisible">
<$transclude/>
</$set>
</$list>
</div>
</$reveal>
</$reveal>",
            "title": "$:/CleverNote/ui/EditTemplateOptions",
            "tags": "$:/tags/EditTemplate",
            "modified": "20171209092944991",
            "created": "20171209084259625"
        },
        "$:/CleverNote/EditTemplateOptions/Type": {
            "text": "<$reveal type="match" state="$:/state/edittemplatetype" text="show">

<$button set="$:/state/edittemplatetype" setTo="hide" class="tc-btn-invisible">Hide type</$button>

</$reveal>
<$reveal type="match" state="$:/state/edittemplatetype" text="hide">

<$button set="$:/state/edittemplatetype" setTo="show" class="tc-btn-invisible">Show type</$button>

</$reveal>",
            "title": "$:/CleverNote/EditTemplateOptions/Type",
            "tags": "$:/tags/EditTemplateOptions",
            "modified": "20171209091655852",
            "created": "20171209091552441"
        },
        "$:/CleverNote/EditTemplateOptions/Fields": {
            "text": "<$reveal type="match" state="$:/state/edittemplatefield" text="show">

<$button set="$:/state/edittemplatefield" setTo="hide" class="tc-btn-invisible">Hide fields</$button>

</$reveal>
<$reveal type="match" state="$:/state/edittemplatefield" text="hide">

<$button set="$:/state/edittemplatefield" setTo="show" class="tc-btn-invisible">Show fields</$button>

</$reveal>",
            "title": "$:/CleverNote/EditTemplateOptions/Fields",
            "tags": "$:/tags/EditTemplateOptions",
            "modified": "20171209091307079",
            "created": "20171209090536631"
        },
        "$:/CleverNote/buttons/search": {
            "text": "<$button class="zaah-btn-square">{{$:/core/images/advanced-search-button}}
<$reveal type="match" state="$:/state/mobile-searchbar" text="yes">
<$action-setfield $tiddler="$:/state/mobile-searchbar" $value="no"/>
</$reveal>
<$reveal type="match" state="$:/state/mobile-searchbar" text="no">
<$action-setfield $tiddler="$:/state/mobile-searchbar" $value="yes"/>
</$reveal>
</$button>",
            "title": "$:/CleverNote/buttons/search",
            "modified": "20171209045420192",
            "created": "20171209032805603"
        },
        "$:/state/edittemplatefield": {
            "text": "hide",
            "title": "$:/state/edittemplatefield",
            "tags": "",
            "modified": "20171209091423714",
            "created": "20171209090923099"
        },
        "$:/state/edittemplatetype": {
            "text": "show",
            "title": "$:/state/edittemplatetype",
            "tags": "",
            "modified": "20171209091705925",
            "created": "20171209091700179"
        },
        "$:/state/mobile-searchbar": {
            "text": "no",
            "title": "$:/state/mobile-searchbar",
            "tags": "",
            "modified": "20171209060725753",
            "created": "20171209040113336"
        },
        "$:/state/sidebar": {
            "text": "no",
            "title": "$:/state/sidebar",
            "tags": "",
            "modified": "20171209092920438",
            "created": "20171208121131791"
        }
    }
}
